import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Login from './Login';
import Register from './Register';
import { AuthContext } from '../AuthContext';

export default function Home(){
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [tab, setTab] = useState('login'); // 'login' or 'register'

  useEffect(()=>{
    if(user && user.username) navigate('/appointments');
  },[user, navigate]);

  const cardStyle = {width: '780px', maxWidth: '95%', margin: '40px auto', borderRadius:10, boxShadow:'0 6px 18px rgba(0,0,0,0.06)', overflow:'hidden', background:'#fff'};
  const headerStyle = {display:'flex', justifyContent:'center', gap:8, padding:12, background:'#f7f7f7'};
  const tabBtn = (t) => ({padding:'8px 16px', borderRadius:6, border:'none', cursor:'pointer', background: tab===t ? '#1976d2' : 'transparent', color: tab===t ? '#fff' : '#333'});

  return (
    <div style={{padding:16}}>
      <div style={cardStyle}>
        <div style={headerStyle}>
          <button style={tabBtn('login')} onClick={()=>setTab('login')}>Login</button>
          <button style={tabBtn('register')} onClick={()=>setTab('register')}>Register</button>
        </div>
        <div style={{position:'relative', height: 'auto', minHeight: '260px'}}>
          <div style={{display:'flex', width:'200%', transform: tab==='login' ? 'translateX(0%)' : 'translateX(-50%)', transition:'transform 300ms ease'}}>
            <div style={{width:'50%', padding:20, boxSizing:'border-box'}}>
              <Login />
            </div>
            <div style={{width:'50%', padding:20, boxSizing:'border-box', borderLeft:'1px solid #eee'}}>
              <Register />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
