import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom';
import { saveSession } from '../api';

export default function Login(){
  const [username,setUsername]=useState('');
  const [password,setPassword]=useState('');
  const [msg,setMsg]=useState('');
  const navigate = useNavigate();

  const submit = async (e)=>{
    e.preventDefault();
    const res = await fetch('http://localhost:8080/api/v1/users/login',{method:'POST', headers:{'Content-Type':'application/json'}, body:JSON.stringify({username,password})});
    if(res.status===200){
      const user = await res.json();
      saveSession(user);
      setMsg('Login successful: '+user.username);
      navigate('/');
      window.location.reload();
    } else {
      setMsg('Login failed');
    }
  }

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={submit}>
        <div className="form-row">
          <label>Username</label><br/>
          <input value={username} onChange={e=>setUsername(e.target.value)} />
        </div>
        <div className="form-row">
          <label>Password</label><br/>
          <input type="password" value={password} onChange={e=>setPassword(e.target.value)} />
        </div>
        <button className="btn" type="submit">Login</button>
      </form>
      {msg && <div className={`message ${msg.startsWith('Login successful')? 'success':'error'}`}>{msg}</div>}
    </div>
  );
}
