import React, {useEffect, useState} from 'react';
import { fetchAppointments, updateAppointment } from '../api';

export default function Appointments(){
  const [appointments,setAppointments]=useState([]);
  useEffect(()=>{ fetchAppointments().then(setAppointments).catch(()=>setAppointments([])); },[]);
  const [resolved, setResolved] = useState({});

  useEffect(()=>{
    (async ()=>{
      const users = {};
      const coaches = {};
      for(const a of appointments){
        if(a.userId && !users[a.userId]){
          const u = await fetchUser(a.userId);
          users[a.userId] = u;
        }
        if(a.coachId && !coaches[a.coachId]){
          const c = await fetchCoach(a.coachId);
          coaches[a.coachId] = c;
        }
      }
      setResolved({users, coaches});
    })();
  },[appointments]);

  const takeAction = async (id, action)=>{
    await updateAppointment(id, {status: action, coachComments: action==='ACCEPTED'? 'Accepted':'Rejected'});
    fetchAppointments().then(setAppointments);
  }

  return (
    <div>
      <h2>Appointments</h2>
      {appointments.length===0 && <div>No appointments found</div>}
      {appointments.map(a=> (
        <div key={a.id} className="card">
          <div style={{display:'flex', justifyContent:'space-between'}}>
            <div>
              <div><strong>#{a.id}</strong> <span className="muted">{a.status}</span></div>
              <div>User: {resolved.users && resolved.users[a.userId]?.username || a.userId}</div>
              <div>Coach: {resolved.coaches && resolved.coaches[a.coachId]?.username || a.coachId}</div>
              <div>Date: {a.appointmentDate} Slot: {a.slot}</div>
            </div>
            <div>
              {a.status==='PENDING' && (
                <>
                  <button className="btn" onClick={()=>takeAction(a.id, 'ACCEPTED')}>Accept</button>
                  <button className="btn secondary" onClick={()=>takeAction(a.id, 'REJECTED')}>Reject</button>
                </>
              )}
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}
