import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Login from './Login';
import Register from './Register';
import { AuthContext } from '../AuthContext';

export default function Home(){
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [tab, setTab] = useState('login'); // 'login' or 'register'

  useEffect(()=>{
    if(user && user.username) navigate('/appointments');
  },[user, navigate]);

  return (
    <div style={{padding:16}}>
      <div className="auth-card">
        <div className="auth-tabs">
          <button className={`auth-tab ${tab==='login'?'active':''}`} onClick={()=>setTab('login')}>Login</button>
          <button className={`auth-tab ${tab==='register'?'active':''}`} onClick={()=>setTab('register')}>Register</button>
        </div>
        <div className="auth-body">
          <div className="auth-pane" style={{width:'50%', transform: tab==='login' ? 'translateX(0%)' : 'translateX(-100%)', transition:'transform 280ms ease'}}>
            <Login />
          </div>
          <div className="auth-pane" style={{width:'50%', transform: tab==='login' ? 'translateX(100%)' : 'translateX(0%)', transition:'transform 280ms ease'}}>
            <Register />
          </div>
        </div>
      </div>
    </div>
  );
}
