import React, {useEffect, useState} from 'react';
import { fetchAppointments, updateAppointment } from '../api';

export default function Appointments(){
  const [appointments,setAppointments]=useState([]);
  useEffect(()=>{ fetchAppointments().then(setAppointments).catch(()=>setAppointments([])); },[]);

  const takeAction = async (id, action)=>{
    await updateAppointment(id, {status: action, coachComments: action==='ACCEPTED'? 'Accepted':'Rejected'});
    fetchAppointments().then(setAppointments);
  }

  return (
    <div style={{padding:20}}>
      <h2>Appointments</h2>
      {appointments.length===0 && <div>No appointments found</div>}
      <ul>
        {appointments.map(a=> (
          <li key={a.id}>
            #{a.id} user:{a.userId} coach:{a.coachId} date:{a.appointmentDate} slot:{a.slot} status:{a.status}
            {a.status==='PENDING' && (
              <>
                <button onClick={()=>takeAction(a.id, 'ACCEPTED')}>Accept</button>
                <button onClick={()=>takeAction(a.id, 'REJECTED')}>Reject</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
